import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import Loading from '../components/Loading';
import getMusics from '../services/musicsAPI';
import MusicCard from '../components/MusicCard';

class Album extends Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      resolve: false,
      dataMusics: [],
    };
  }

  componentDidMount() {
    this.fetchMusics();
  }

  fetch = () => {
    const { match: { params: { id } } } = this.props;

    this.setState({ loading: true }, async () => {
      const dataMusics = await getMusics(id);

      this.setState({
        loading: false,
        resolve: true,
        dataMusics,
      });
    });
  }

  renderMusics = () => {
    const {
      resolve,
      dataMusics,
    } = this.state;

    if (resolve) {
      return (
        <section className="container-musics">
          <div>
            <img
              src={ dataMusics[0].artworkUrl100 }
              alt={ dataMusics[0].collectionName }
            />
            <h4 data-testid="album-name">{dataMusics[0].collectionName}</h4>
            <p data-testid="artist-name">{dataMusics[0].artistName}</p>
          </div>
          <div>
            {dataMusics.slice(1).map((musics) => (
              <MusicCard
                key={ musics.trackNumber }
                musics={ musics }
              />
            ))}
          </div>
        </section>
      );
    }
  }

  render() {
    const { loading } = this.state;
    return (
      <div data-testid="page-album">
        <Header />
        { loading ? <Loading /> : this.renderMusics() }
      </div>
    );
  }
}

Album.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.string,
    }),
  }).isRequired,
};

export default Album;
