import React from 'react';
import searchAlbumsAPI from '../services/searchAlbumsAPI';
import CardList from './CardList';
import Header from '../components/Header';
import Loading from '../components/Loading';

class Search extends React.Component {
  constructor() {
    super();
    this.state = {
      artistSearch: '',
      loading: false,
      enableArtist: false,
      artistName: '',
      arrayAlbuns: [],
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange = (event) => {
    const { artistSearch, value } = event.target;
    this.setState({
      [artistSearch]: value,
    });
  }

  async handleClick() {
    this.setState({
      loading: true,
    });
    const wait = await searchAlbumsAPI(artistSearch);
    this.setState({
      loading: false,
      enableArtist: true,
      artistName: artistSearch,
      artistSearch: '',
      arrayAlbuns: [...wait],
    });
  }

  render() {
    const { artistSearch, loading, artistName, enableArtist, arrayAlbuns } = this.state;
    const minLengthForArtistName = 2;
    const forms = (
      <form>
        <label htmlFor="artistSearch">
          Nome do Artista
          <input
            type="text"
            name="artistSearch"
            value={ artistSearch }
            data-testid="search-artist-input"
            onChange={ this.handleChange }
          />
        </label>
        <button
          type="button"
          data-testid="search-artist-button"
          disabled={ artistSearch.length < minLengthForArtistName }
          onClick={ () => this.handleClick(artistSearch) }
        >
          Pesquisar
        </button>
      </form>
    );
    return (
      <div data-testid="page-search">
        <Header />
        {loading ? <Loading /> : forms}
        {!loading && enableArtist ? `Resultado de álbuns de: ${artistName}` : '' }
        {!loading && enableArtist ? <CardList albumList={ [...arrayAlbuns] } /> : ''}
      </div>
    );
  }
}

export default Search;
