import React from 'react';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import { getMusics } from '../services/musicsAPI';
import MusicCard from '../components/MusicCard';

class Album extends React.Component {
  constructor() {
    super();
    this.state = {
      musicList: [],
    };
    this.fetchMusicUrl = this.fetchUrl.bind(this);
  }

  componentDidMount() {
    this.fetchMusicUrl();
  }

  async fetchMusicUrl() {
    const { match: { params: { id } } } = this.props;
    const musics = await getMusics(id);
    this.setState({
      musicList: [...musics],
      artistName: musics[0].artistName,
      collectioName: musics[0].collectioName,
      collectionIMG: musics[0].artworkUrl100,
    });
  }

  render() {
    const { loading, musicList, artistName, collectioName, collectionIMG } = this.state;
    return (
      <>
        <Header />
        <div data-testid="page-album">
          <h2>Album</h2>
          <div>
            <img src={ collectionIMG } alt="album" />
            <h4 data-testid="artist-name">{artistName}</h4>
            <h6 data-testid="album-name">{collectioName}</h6>
            { musicList.slice(1)
              .map((music, index) => <MusicCard key={ index } albumTracks={ music } />)}
          </div>
        </div>
      </>
    );
  }
}

Album.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.string.isRequired,
    }),
  }).isRequired,
};

export default Album;
