import React from 'react';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import { getMusics } from '../services/musicsAPI';
import MusicCard from '../components/MusicCard';

class Album extends React.Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      id,
      namesOfArtists: '',
      collectionNames: '',
      musicList: [],
    };
  }

  componentDidMount() {
    this.musicLoadingList();
  }

  musicLoadingList = async () => {
    const { id, loading } = this.state;
    if (!loading) {
      const result = await getMusics(id);
      this.setState({
        loading: true,
        musicList: result,
      });
    }
    this.artistsNames();
  }

  artistsNames = () => {
    const { musicList, loading } = this.state;
    if (loading) {
      const namesOfArtists = musicList[0].artistName;
      const collectionNames = musicList[0].collectionName;
      this.setState({
        namesOfArtists,
        collectionNames,
      });
    }
  }

  render() {
    const { loading, musicList, namesOfArtists, collectionNames } = this.state;
    return (
      <div data-testid="page-album">
        <Header />
        <h2>Album</h2>
        <h4 data-testid="artist-name">{namesOfArtists}</h4>
        <h6 data-testid="album-name">{collectionNames}</h6>
        { loading && <MusicCard albumTracks={ musicList } />}
      </div>
    );
  }
}

Album.propTypes = {
  match: PropTypes.objectOf({
    params: PropTypes.objectOf({
      id: PropTypes.string,
    }),
  }).isRequired,
};

export default Album;
