import React from 'react';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import { getMusics } from '../services/musicsAPI';
import MusicCard from '../components/MusicCard';

class Album extends React.Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      id,
      namesOfArtists: '',
      collectionNames: '',
      musicList: [],
    };
    this.fetchMusicUrl = this.fetchUrl.bind(this);
  }

  componentDidMount() {
    this.fetchMusicUrl();
  }

  async fetchMusicUrl() {
    const { match: { params: { id } } } = this.props;
    const musics = await getMusics(id);
    this.setState({
      musicList: [...musics],
      artistName: musics[0].artistName,
      collectioName: musics[0].collectioName,
      collectionIMG: musics[0].artworkUrl100,
    })
  }

  render() {
    const { loading, musicList, artistName, collectioName, coll } = this.state;
    return (
      <div data-testid="page-album">
        <Header />
        <h2>Album</h2>
        <h4 data-testid="artist-name">{artistName}</h4>
        <h6 data-testid="album-name">{collectioName, coll}</h6>
        { loading && <MusicCard albumTracks={ musicList } />}
      </div>
    );
  }
}

Album.propTypes = {
  match: PropTypes.objectOf({
    params: PropTypes.objectOf({
      id: PropTypes.string,
    }),
  }).isRequired,
};

export default Album;
